mapper.select:
SELECT `article`.`index` AS `index`, `article`.`user_email` AS `userEmail`, `article`.`board_id` AS `boardId`, `article`.`title` AS `title`, `article`.`thumbnail` AS `thumbnail`, `article`.`view` AS `view`, `article`.`written_on` AS `writtenOn`, `article`.`modified_on` AS `modifiedOn`, `product`.`quantity` AS `qunantity`, `product`.`price` AS `price`, `product`.`category_text` AS 'categoryText' FROM `twd_bbs`.`articles` AS `article` LEFT JOIN `twd_member`.`users` AS `user` ON `user`.`email` = `article`.`user_email` LEFT JOIN `twd_shop`.`sale_product` AS `product` ON `article`.`index` = `product`.`article_index` WHERE `product`.`category_text` = #{boardId}
AND REPLACE(`article`.`title`,' ','') LIKE CONCAT('%',REPLACE(#{keyword},' ',''),'%')
AND (REPLACE(`article`.`title`,' ','') LIKE CONCAT('%',REPLACE(#{keyword},' ',''),'%') OR REPLACE(`article`.`content`,' ','') LIKE CONCAT('%',REPLACE(#{keyword},' ',''),'%'))
ORDER BY `article`.`index` DESC LIMIT #{limit} OFFSET #{offset}
